$.get('http://localhost:3000/public/maps/romania.geojson', function (res) {
  echarts.registerMap('Romania', res);

  queried = data.series.map(d => {
    return {
      'name': d.fields[1].labels['ISO'],
      'value': d.fields[1].values.buffer[0].toFixed(2)
    }
  })

  queried = queried.map(d => {
		return {
      'name': d['name'],
      'value': parseFloat(d['value'])
    }  
  })

  const min = queried.reduce((prev, curr) => prev['value'] < curr['value'] ? prev : curr)['value']
  const max = queried.reduce((prev, curr) => prev['value'] > curr['value'] ? prev : curr)['value']

  options = {
    tooltip: {
      trigger: 'item',
      showDelay: 0,
      transitionDuration: 0.2,
      padding: 10,
      formatter: '{a}<br>Megye: {b}<br>√Åtlag √°r: {c} RON'
    },
    visualMap: {
      left: 'right',
			top: 'top',
      min: min - 0.05,
      max: max + 0.05,
      text: ['RON'],
      formatter: val => val.toFixed(2),
      textStyle: {
        	'color': '#ccc'
      },
      inRange: {
        color: ['#ffffb2','#fed976','#feb24c','#fd8d3c','#f03b20','#bd0026']
      },
      calculable: true,
      realtime: true
    },
    toolbox: {
      show: false,
    },
    series: [ 
      {
        name: 'üìà Legnagyobb √ºzemanyag√°rak (95-√∂s benzin)',
        nameProperty: 'shapeISO',
        type: 'map',
        map: 'Romania',
        data: queried,
        itemStyle: {
          borderColor: '#444',
          borderWidth: '0.75'
        },
      	emphasis: {
          label: {
            color: '#fff'
          },
          itemStyle: {
            areaColor: null
          }
        },
      }
    ],
  }	

  echartsInstance.setOption(options)
})

