$.get('public/maps/romania.geojson', function (res) {
  echarts.registerMap('Romania', res);

  queried = data.series.map(d => {
    return {
      'name': d.fields[1].labels['ISO'],
      'value': d.fields[1].values.buffer[0].toFixed(2)
    }
  })

  queried = queried.map(d => {
		return {
      'name': d['name'],
      'value': parseFloat(d['value'])
    }  
  })

  const min = queried.reduce((prev, curr) => prev['value'] < curr['value'] ? prev : curr)['value']
  const max = queried.reduce((prev, curr) => prev['value'] > curr['value'] ? prev : curr)['value']

  options = {
    tooltip: {
      trigger: 'item',
      showDelay: 0,
      transitionDuration: 0.2,
      padding: 10,
      formatter: 'Megye: {b}<br>√Åtlag √°r: {c} RON',
      backgroundColor: 'rgba(34, 37, 43, 1)',
      textStyle: {
        fontSize: 13,
        fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
        color: 'rgb(204, 204, 220)'
      },
      extraCssText: 'border-radius: 2px; box-shadow: 0 1px 1px rgba(0,0,0,0.08), 0 2px 2px rgba(0,0,0,0.12), 0 4px 4px rgba(0,0,0,0.16), 0 8px 8px rgba(0,0,0,0.20);'
    },
    visualMap: {
      left: 'right',
			top: 'top',
      min: min - 0.05,
      max: max + 0.05,
      text: ['RON'],
      formatter: val => val.toFixed(2),
      textStyle: {
        	'color': '#ccc'
      },
      inRange: {
        color: ['#edf8e9','#bae4b3','#74c476','#31a354','#006d2c']
      },
      calculable: true,
      realtime: true
    },
    toolbox: {
      show: false,
    },
    series: [ 
      {
        name: 'üìà Legnagyobb √ºzemanyag√°rak (95-√∂s benzin)',
        nameProperty: 'shapeISO',
        type: 'map',
        map: 'Romania',
        data: queried,
        itemStyle: {
          borderColor: '#444',
          borderWidth: '0.75'
        },
      	emphasis: {
          label: {
            color: '#fff'
          },
          itemStyle: {
            areaColor: null
          }
        },
      }
    ],
  }	

  echartsInstance.setOption(options)
})

